preferences.postscript:
if (Translator.BetterCSLYAML) {
  if (zotero.extraFields.raw.entrysubtype === 'classical') csl.type = 'classic';
  if (zotero.extraFields.raw.entrysubtype === 'classical') delete csl.author;
  if (zotero.language === 'english') csl.language = 'en';
}

if (Translator.BetterBibLaTeX) {
  if (zotero.extraFields.raw.entrytype === 'bookinbook') {
    tex.add({name: 'booktitle', value: tex.has.journaltitle.value});
    tex.remove('journaltitle');
  }
}
